import React, { useEffect, useState, useRef } from "react";
import { InputText } from "primereact/inputtext";
import { Checkbox } from "primereact/checkbox";
import { Dialog } from "primereact/dialog";
import { Toast } from "primereact/toast";
import { addTeacher } from "../../../../services/firebase/collection/teacher";
import { Button } from "primereact/button";
import { generateId } from "../../../../utils/generateId";

const TeacherModal = ({ hideDialog, teacherEdit, showDialog }) => {
  const [teacher, setTeacher] = useState({
    id: "",
    name: "",
    active: true,
  });
  const toast = useRef(null);

  useEffect(() => {
    setTeacher({
      ...teacherEdit,
      id: teacherEdit.id ? teacherEdit.id : generateId(),
    });
  }, [teacherEdit]);

  const saveTeacher = async () => {
    try {
      const response = await addTeacher(teacher, teacher.id);
      console.log(response);
      hideDialog();
      toast.current.show({
        severity: "success",
        summary: "Éxito",
        detail: "Ocupación guardada con éxito",
        life: 3000,
      });
      setTeacher({
        id: "",
        name: "",
        active: true,
      });
    } catch (e) {
      console.log(e);
    }
  };

  const handleEditTeacher = (event) => {
    const { name, value } = event.target;
    const { lastname, value2 } = event.target;
    const teacherCopy = { ...teacher };
    teacherCopy[name] = value;
    teacherCopy[lastname] = value2;
    setTeacher(teacherCopy);
    console.log(teacher);
  };
  const handleCheckChange = (event) => {
    const { checked, value } = event;
    const teacherCopy = { ...teacher };
    teacherCopy[value] = checked;
    setTeacher(teacherCopy);
    console.log(teacher);
  };

  const teacherDialogFooter = (
    <React.Fragment>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={hideDialog}
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={saveTeacher}
      />
    </React.Fragment>
  );

  return (
    <>
      <Toast ref={toast} />
      <Dialog
        visible={showDialog}
        style={{ width: "450px" }}
        header="Detalles del Profesor"
        modal
        className="p-fluid"
        footer={teacherDialogFooter}
        onHide={hideDialog}
      >
        <div className="p-field">
          <label htmlFor="name">Nombre:</label>
          <InputText
            id="name"
            name="name"
            value={teacher.name}
            onChange={handleEditTeacher}
            required
            autoFocus
            // className={classNames({
            //   "p-invalid": submitted && !teacher.name,
            // })}
          />
          {/*{submitted && !product.name && (*/}
          {/*  <small className="p-error">Name is required.</small>*/}
          {/*)}*/}
        </div>
        <div className="p-field">
          <div className="p-formgrid p-grid">
            <Checkbox
              inputId="active"
              value="active"
              checked={teacher.active}
              onChange={handleCheckChange}
            />
            <label htmlFor="active">Activo</label>
          </div>
        </div>
      </Dialog>
    </>
  );
};
export default TeacherModal;
