import { InputText } from 'primereact/inputtext';
import {Password} from 'primereact/password';
import { Button } from 'primereact/button';
import { SignInAdmin } from "../services/firebase/auth";
import { getUserById } from "../services/firebase/operations/users";
import {useContext, useEffect, useState} from "react";
import {LOGIN_PAGE, OCUPATION_PAGE} from "../constants/routes";
import "../App.css";

export default ({ history }) => {
    const SignIn = () => {
        const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [error, setError] = useState(null);
        const signInWithEmailAndPasswordHandler =
            (event, email, password) => {
                event.preventDefault();
            };

        return (
            <div className="login-container">
                <div className="login-div">
                    <h1 className="login-title">Iniciar sesión</h1>
                    <span className="p-float-label">
                    <InputText id="in" className="w-100" onChange={(event) => setEmail(value)}/>
                    <label htmlFor="in">Email</label>
                </span>
                    <span className="p-float-label">
                    <Password toggleMask feedback={false} id="inPassword" onChange={(event) => setPassword(value)}/>
                    <label htmlFor="inPassword">Contraseña</label>
                </span>
                    <div className="forgot-password-div">
                        <a href="./">Olvidé mi contraseña</a>
                    </div>
                    <div className="sign-in-button-div">
                        <Button type="button" label="Ingresar" className="login-button"/>
                    </div>
                </div>
            </div>
        );
    }
};

//export default Login;
