import { InputText } from "primereact/inputtext";
import { Password } from "primereact/password";
import { AuthContext } from "../../App";
import { useContext, useState } from "react";
import SideBarMenu from "../../components/SideBarMenu";
import { Button } from "primereact/button";
import "../App.css";
import { addUser } from "../../services/firebase/collection/user";
import { OCUPATION_PAGE } from "../../constants/routes";

const Ocupation = () => {
  const { user } = useContext(AuthContext);
  const [id, setId] = useState("");
  const [full_name, setFull_name] = useState("");
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("");
  const createUser = async (event) => {
    try {
      const response = await AddUser(email, password);
      if (response.user.uid) {
        setUser(response.user);
        history.push(OCUPATION_PAGE);
      }
      console.log(response);
    } catch (error) {
      console.log(error);
    }
    event.preventDefault();
  };

  return (
    <div className="login-container">
      <div className="login-div">
        <h1 className="login-title">Iniciar sesi√≥n</h1>
        <span className="p-float-label input-span">
          <InputText
            id="in"
            className="w-100 p-inputtext-sm p-d-block p-mb-2"
            onChange={(event) => setId(event.target.value)}
          />
          <label htmlFor="in">Id</label>
        </span>
        <span className="p-float-label input-span">
          <InputText
            id="in"
            className="w-100 p-inputtext-sm p-d-block p-mb-2"
            onChange={(event) => setFull_name(event.target.value)}
          />
          <label htmlFor="in">Full Name</label>
        </span>
        <span className="p-float-label input-span">
          <InputText
            id="in"
            className="w-100 p-inputtext-sm p-d-block p-mb-2"
            onChange={(event) => setEmail(event.target.value)}
          />
          <label htmlFor="in">Email</label>
        </span>
        <span className="p-float-label input-span">
          <InputText
            id="in"
            className="w-100 p-inputtext-sm p-d-block p-mb-2"
            onChange={(event) => setRole(event.target.value)}
          />
          <label htmlFor="in">Role</label>
        </span>
        <div className="sign-in-button-div">
          <Button
            type="button"
            label="Guardar"
            className="login-button"
            onClick={createUser}
          />
        </div>
      </div>
    </div>
  );
  //return <SideBarMenu />;
};

export default Ocupation;
