import React, { useContext, useState, useEffect } from "react";
import { InputText } from "primereact/inputtext";
import { AuthContext } from "../../../App";
import { classNames } from "primereact/utils";
import {
  addOcupation,
  getAllOcupations,
  listenOcupations,
} from "../../../services/firebase/collection/teacher";
import { TEACHER_PAGE } from "../../../constants/routes";
import Table from "../../../components/Table";
import { Column } from "primereact/column";
import { Button } from "primereact/button";
import "./components/teacher.scss";
import { values } from "pg/lib/native/query";
import TeacherModal from "./components/TeacherModal";
import { Checkbox } from "primereact/checkbox";

const Teacher = () => {
  const { user } = useContext(AuthContext);
  const [ocupation, setOcupation] = useState({
    id: "",
    name: "",
    active: true,
  });
  const [ocupations, setOcupations] = useState([]);
  const [globalFilter, setGlobalFilter] = useState(null);
  const [showDialog, setShowDialog] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    async function fetchData() {
      const ocupationsFetched = await getAllOcupations();
      return { ocupationsFetched };
    }

    fetchData()
      .then((data) => {
        const { ocupationsFetched } = data;
        setOcupations(ocupationsFetched.docs.map((d) => d.data()));
      })
      .catch((error) => {
        console.log(error);
      });
    listenOcupationChange();
  }, []);

  const listenOcupationChange = () => {
    listenOcupations((ocupationsFetched) => {
      setOcupations(ocupationsFetched.docs.map((d) => d.data()));
      console.log(ocupationsFetched);
    });
  };

  const openNew = () => {
    setOcupation({
      id: "",
      name: "",
      active: true,
    });
    setSubmitted(false);
    setShowDialog(true);
  };
  const hideDialog = () => {
    setSubmitted(false);
    setShowDialog(false);
  };
  const editOcupation = (ocupation) => {
    setOcupation({ ...ocupation });
    setShowDialog(true);
  };
  const actionBodyTemplate = (rowData) => {
    return (
      <>
        <Button
          icon="pi pi-pencil"
          className="p-button-rounded p-button-success p-mr-2"
          onClick={() => editOcupation(rowData)}
        />
      </>
    );
  };
  const activeBodyTemplate = (status) => {
    return <Checkbox disabled />;
  };
  return (
    <>
      <Table
        data={ocupations}
        rows={10}
        dataKey="id"
        emptyMessage="No existen registros"
        tableName="Profesores"
        setGlobalFilter={setGlobalFilter}
        openNew={openNew}
      >
        <Column field="name" header="Nombre" sortable />
        <Column field="active" header="Activo" body={activeBodyTemplate} />
        <Column body={actionBodyTemplate}></Column>
      </Table>
      <TeacherModal
        showDialog={showDialog}
        ocupationEdit={ocupation}
        hideDialog={hideDialog}
      />
    </>
  );
};

export default Teacher;
