import React, { useEffect, useState, useRef } from "react";
import { InputText } from "primereact/inputtext";
import { Checkbox } from "primereact/checkbox";
import { Dialog } from "primereact/dialog";
import { Toast } from "primereact/toast";
import { addStudent } from "../../../../services/firebase/collection/student";
import { Button } from "primereact/button";
import { generateId } from "../../../../utils/generateId";
import { InputNumber } from "primereact/inputnumber";

const StudentModal = ({ hideDialog, studentEdit, showDialog }) => {
  const [student, setStudent] = useState({
    id: "",
    name: "",
    active: true,
  });
  const toast = useRef(null);

  useEffect(() => {
    setStudent({
      ...studentEdit,
      id: studentEdit.id ? studentEdit.id : generateId(),
    });
  }, [studentEdit]);

  const saveStudent = async () => {
    try {
      const response = await addStudent(student, student.id);
      console.log(response);
      hideDialog();
      toast.current.show({
        severity: "success",
        summary: "Ã‰xito",
        detail: "Estudiante guardado con Ã©xito",
        life: 3000,
      });
      setStudent({
        id: "",
        name: "",
        active: true,
      });
    } catch (e) {
      console.log(e);
    }
  };

  const handleEditStudent = (event) => {
    const { name, value } = event.target;
    const studentCopy = { ...student };
    studentCopy[name] = value;
    setStudent(studentCopy);
    console.log(student);
  };
  const handleCheckChange = (event) => {
    const { checked, value } = event;
    const studentCopy = { ...student };
    studentCopy[value] = checked;
    setStudent(studentCopy);
    console.log(student);
  };

  const studentDialogFooter = (
    <React.Fragment>
      <Button
        label="Cancelar"
        icon="pi pi-times"
        className="p-button-text"
        onClick={hideDialog}
      />
      <Button
        label="Guardar"
        icon="pi pi-check"
        className="p-button-text"
        onClick={saveStudent}
      />
    </React.Fragment>
  );

  return (
    <>
      <Toast ref={toast} />
      <Dialog
        visible={showDialog}
        style={{ width: "450px" }}
        header="Datos del estudiante"
        modal
        className="p-fluid"
        footer={countryDialogFooter}
        onHide={hideDialog}
      >
        <div className="p-field">
          <label htmlFor="name">Nombre:</label>
          <InputText
            id="name"
            name="name"
            value={country.name}
            onChange={handleEditStudent}
            required
            autoFocus
          />
        </div>
          <div className="flex align-items-center">
            <Checkbox
              inputId="active"
              value="active"
              checked={country.active}
              onChange={handleCheckChange}
            />
            <label htmlFor="active">Activo</label>
          </div>
      </Dialog>
    </>
  );
};
export default StudentModal;
